import './Login.css';
import { useNavigate } from 'react-router-dom';
import { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';

export default function Login() {
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);

  const handleLogin = (e) => {
    e.preventDefault();
    login(); // ‚úÖ auth success
    navigate('/dashboard');
  };

  return (
    <div className="login">
      <h1>üç∑ LogAnalyzer</h1>

      <form onSubmit={handleLogin}>
        <input placeholder="üìß Email" />
        <input type="password" placeholder="üîê Password" />
        <button>Login üöÄ</button>
      </form>
    </div>
  );
}
