import './Upload.css';
import { useState } from "react";
import { uploadLog } from "../api";

export default function Upload() {
  const [file, setFile] = useState(null);
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a log file üìÑ");
      return;
    }

    if (!email) {
      alert("Please enter email üìß");
      return;
    }

    try {
      setLoading(true);
      await uploadLog(file, email);
      alert("File uploaded successfully ‚úÖ");
      setFile(null);
      setEmail("");
    } catch (err) {
      console.error(err);
      alert("Upload failed ‚ùå");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="upload-page">
      <h1>üì§ Upload Log File</h1>

      <div className="upload-box">
        <input
          type="email"
          placeholder="User Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <input
          type="file"
          accept=".log,.txt"
          onChange={(e) => setFile(e.target.files[0])}
        />

        <button onClick={handleUpload} disabled={loading}>
          {loading ? "Uploading..." : "Upload üöÄ"}
        </button>
      </div>
    </div>
  );
}
