// src/pages/Dashboard.jsx
import { useState } from 'react'; // no need to import 'React' explicitly if using modern React
import Card from '../components/Card';
import FileUpload from '../components/FileUpload';
import { BarChart, Bar, XAxis, YAxis, Tooltip, PieChart, Pie, Cell } from 'recharts';

const logData = [
  { type: 'Errors âŒ', count: 12 },
  { type: 'Warnings âš ï¸', count: 34 },
  { type: 'Info â„¹ï¸', count: 56 }
];

const pieData = [
  { name: 'Errors âŒ', value: 12 },
  { name: 'Warnings âš ï¸', value: 34 },
  { name: 'Info â„¹ï¸', value: 56 }
];

const COLORS = ['#ff0000', '#ff9900', '#0099ff'];

export default function Dashboard() {
  const handleUpload = (file) => {
    console.log('Uploaded:', file);
    alert(`ðŸ“‚ ${file.name} uploaded successfully!`);
  };

  return (
    <div>
      <h1 style={{ color: '#800000' }}>Hello Dashboard ðŸ‘‹</h1>
      <FileUpload onUpload={handleUpload} />

      <div style={{ display: 'flex', gap: '20px', marginTop: '20px' }}>
        <Card title="Errors" count={12} emoji="âŒ" color="#ff0000" />
        <Card title="Warnings" count={34} emoji="âš ï¸" color="#ff9900" />
        <Card title="Info" count={56} emoji="â„¹ï¸" color="#0099ff" />
      </div>

      <div style={{ display: 'flex', justifyContent: 'center', gap: '50px', marginTop: '30px' }}>
        <div>
          <h3>ðŸ“ˆ Bar Chart</h3>
          <BarChart width={400} height={300} data={logData}>
            <XAxis dataKey="type" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="count" fill="#800000" />
          </BarChart>
        </div>
        <div>
          <h3>ðŸ¥§ Pie Chart</h3>
          <PieChart width={400} height={300}>
            <Pie
              data={pieData}
              cx={200}
              cy={150}
              outerRadius={100}
              fill="#8884d8"
              dataKey="value"
              label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
            >
              {pieData.map((entry, index) => (
                <Cell key={index} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip />
          </PieChart>
        </div>
      </div>
    </div>
  );
}
