// Signup.jsx
import './Login.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import { signup as apiSignup } from '../api';

export default function Signup() {
  const navigate = useNavigate();

  const [form, setForm] = useState({ email: '', password: '' });

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      await apiSignup(form); // Call signup API
      alert('Signup successful üöÄ Please login now');
      navigate('/login'); // Redirect to login page
    } catch (err) {
      alert('Signup failed ‚ùå');
      console.error(err);
    }
  };

  return (
    <div className="login-wrap">
      <div className="login-card">
        <h1>üç∑Join LogAnalyzer</h1>
        <p className="tagline">Create your account üíú</p>

        <form onSubmit={handleSignup}>
          <div className="input-box">
            <span>üë§</span>
            <input
              type="text"
              placeholder="Username"
              required
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="input-box">
            <span>üìß</span>
            <input name="email" type="email" placeholder="Email" required onChange={handleChange} />
          </div>

          <div className="input-box">
            <span>üîê</span>
            <input name="password" type="password" placeholder="Password" required onChange={handleChange} />
          </div>

          <button className="login-btn">Sign Up ‚ú®</button>
        </form>

        <p className="switch">
          Already have an account? <span onClick={() => navigate('/login')}>Login üöÄ</span>
        </p>
      </div>
    </div>
  );
}
