// src/pages/Dashboard.jsx
import React from 'react';
import Card from './Card';
import FileUpload from '../components/FileUpload';
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend, PieChart, Pie, Cell } from 'recharts';
import './Dashboard.css';

const logData = [
  { type: 'Errors âŒ', count: 12 },
  { type: 'Warnings âš ï¸', count: 34 },
  { type: 'Info â„¹ï¸', count: 56 }
];

const pieData = [
  { name: 'Errors âŒ', value: 12 },
  { name: 'Warnings âš ï¸', value: 34 },
  { name: 'Info â„¹ï¸', value: 56 }
];

const COLORS = ['#ff0000', '#ff9900', '#0099ff'];

function Dashboard() {
  const handleUpload = (file) => {
    console.log("Uploaded:", file);
    alert(`ðŸ“‚ ${file.name} uploaded successfully!`);
  };

  return (
    <div className="page dashboard-page">
      <h2>ðŸ“Š Dashboard</h2>

      <FileUpload onUpload={handleUpload} />

      <div className="cards-container">
        <Card title="Errors" count={12} emoji="âŒ" color="#ff0000" />
        <Card title="Warnings" count={34} emoji="âš ï¸" color="#ff9900" />
        <Card title="Info" count={56} emoji="â„¹ï¸" color="#0099ff" />
      </div>

      <h3>ðŸ“ˆ Log Summary (Bar Chart)</h3>
      <BarChart width={500} height={300} data={logData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
        <XAxis dataKey="type" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Bar dataKey="count" fill="#800000" />
      </BarChart>

      <h3>ðŸ¥§ Log Distribution (Pie Chart)</h3>
      <PieChart width={400} height={300}>
        <Pie
          data={pieData}
          cx={200}
          cy={150}
          labelLine={false}
          label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
          outerRadius={100}
          fill="#8884d8"
          dataKey="value"
        >
          {pieData.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
          ))}
        </Pie>
        <Tooltip />
      </PieChart>
    </div>
  );
}

export default Dashboard;
